            return false
        }
        if lhs.`version` != rhs.`version` {
            return false
        }
        if lhs.`versionSignature` != rhs.`versionSignature` {
            return false
        }
        if lhs.`encKeys` != rhs.`encKeys` {
            return false
        }
        if lhs.`encConfig` != rhs.`encConfig` {
            return false
        }
        if lhs.`data` != rhs.`data` {
            return false
        }
        return true
    }

    public func hash(into hasher: inout Hasher) {
        hasher.combine(`did`)
        hasher.combine(`name`)
        hasher.combine(`version`)
        hasher.combine(`versionSignature`)
        hasher.combine(`encKeys`)
        hasher.combine(`encConfig`)
        hasher.combine(`data`)
    }
